mapper.select:
SELECT cp.coach_user_id AS coachUserId, cp.coach_service_area AS serviceArea, cp.coach_teaching_years AS teachingYears, cp.coach_rating_score AS ratingScore, cp.coach_rating_count AS ratingCount, cp.coach_certification_level AS certificationLevel, cp.coach_min_price AS minPrice, cp.is_recommended_coach AS isRecommended,
(6371 * acos( cos(radians(#{latitude})) * cos(radians(cp.coach_latitude)) * cos(radians(cp.coach_longitude) - radians(#{longitude})) + sin(radians(#{latitude})) * sin(radians(cp.coach_latitude)) )) AS distance
NULL AS distance
FROM coach_profiles cp
INNER JOIN ( SELECT DISTINCT coach_user_id FROM coach_course_type WHERE coach_is_active = 1 AND coach_service_type IN
#{type}
) cs ON cp.coach_user_id = cs.coach_user_id
cp.coach_status = 1 AND cp.coach_audit_status = 1
AND ( cp.coach_service_area LIKE CONCAT('%', #{keyword}, '%') )
AND cp.coach_min_price >= #{minPrice}
AND cp.coach_max_price <= #{maxPrice}
AND cp.coach_service_area IN
#{area}
AND (
JSON_CONTAINS(cp.coach_certification_level, JSON_QUOTE(#{cert}))
)
AND cp.coach_teaching_years >= #{minYears}
AND cp.coach_teaching_years <= #{maxYears}
AND cp.coach_gender = #{gender}
HAVING distance <= #{maxDistance}
ORDER BY distance ASC, cp.coach_rating_score DESC
ORDER BY cp.coach_rating_score DESC, cp.coach_rating_count DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(DISTINCT cp.coach_user_id)
FROM coach_profiles cp

    INNER JOIN (
    SELECT DISTINCT coach_user_id
    FROM coach_course_type
    WHERE coach_is_active = 1
    AND coach_service_type IN

        #{type}

    ) cs ON cp.coach_user_id = cs.coach_user_id


    cp.coach_status = 1
    AND cp.coach_audit_status = 1


        AND (
        cp.coach_service_area LIKE CONCAT('%', #{keyword}, '%')
        )



        AND cp.coach_min_price >= #{minPrice}



        AND cp.coach_max_price <= #{maxPrice}



        AND cp.coach_service_area IN

            #{area}




        AND (

            JSON_CONTAINS(cp.coach_certification_level, JSON_QUOTE(#{cert}))

        )



        AND cp.coach_teaching_years >= #{minYears}



        AND cp.coach_teaching_years <= #{maxYears}



        AND cp.coach_gender = #{gender}




        AND (6371 * acos(
        cos(radians(#{latitude})) *
        cos(radians(cp.coach_latitude)) *
        cos(radians(cp.coach_longitude) - radians(#{longitude})) +
        sin(radians(#{latitude})) *
        sin(radians(cp.coach_latitude))
        )) <= #{maxDistance}
