mapper.update:
UPDATE social_note
SET like_count = like_count + 1
WHERE note_id = #{noteId}

UPDATE social_note
SET like_count = GREATEST(like_count - 1, 0)
WHERE note_id = #{noteId}

UPDATE social_note
SET comment_count = comment_count + 1
WHERE note_id = #{noteId}

UPDATE social_note
SET comment_count = GREATEST(comment_count - #{count}, 0)
WHERE note_id = #{noteId}
